@startuml !theme plain skinparam classAttributeIconSize 0 skinparam defaultFontName Arial skinparam defaultFontSize 10
class JwtTokenValidator { -secretKey: String -issuer: String -audience: String -clockSkewSeconds: Integer +validateToken(token: String): ValidationResult +extractClaims(token: String): Claims +isTokenExpired(token: String): Boolean +getTokenSubject(token: String): String +validateIssuer(token: String): Boolean +validateAudience(token: String): Boolean }
class JwtTokenGenerator { -secretKey: String -issuer: String -defaultExpirationHours: Integer +generateToken(subject: String, claims: Map<String, Object>): String +generateTokenForSystem(systemName: String): String +setCustomExpiration(hours: Integer): JwtTokenGenerator +addCustomClaim(key: String, value: Object): JwtTokenGenerator }
class SecurityConfiguration { -id: Long -name: String -jwtSecret: String -jwtIssuer: String -jwtAudience: String -tokenExpirationHours: Integer -clockSkewSeconds: Integer -isActive: Boolean +getId(): Long +getName(): String +getJwtSecret(): String +setJwtSecret(secret: String): void +getJwtIssuer(): String +getJwtAudience(): String +getTokenExpirationHours(): Integer +activate(): void +deactivate(): void }
class ValidationResult { -valid: Boolean -errorMessage: String -claims: Claims -validatedAt: LocalDateTime +isValid(): Boolean +getErrorMessage(): String +getClaims(): Claims +getValidatedAt(): LocalDateTime +markAsValid(claims: Claims): void +markAsInvalid(error: String): void }
class Claims { -subject: String -issuer: String -audience: String -expirationTime: LocalDateTime -issuedAt: LocalDateTime -customClaims: Map<String, Object> +getSubject(): String +getIssuer(): String +getAudience(): String +getExpirationTime(): LocalDateTime +getIssuedAt(): LocalDateTime +getCustomClaim(key: String): Object +addCustomClaim(key: String, value: Object): void +isExpired(): Boolean }
JwtTokenValidator --> ValidationResult : "produces" JwtTokenValidator --> Claims : "extracts" JwtTokenGenerator --> SecurityConfiguration : "uses" JwtTokenValidator --> SecurityConfiguration : "uses" ValidationResult --> Claims : "contains"
@enduml
